import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem("email");

  const handleLogout = () => {
    localStorage.removeItem("email");
    navigate("/logout");
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">üèãÔ∏è‚Äç‚ôÇÔ∏è FitTracker</div>
      <div className="navbar-links">
        <NavLink to="/" className="nav-link">
          üè† Home
        </NavLink>
        <NavLink to="/profile" className="nav-link">
          üë§ Profile
        </NavLink>
        <NavLink to="/daily-challenge" className="nav-link">
          üìÖ Daily
        </NavLink>
        <NavLink to="/select-workout" className="nav-link">
          üìπ Workout
        </NavLink>
        <NavLink to="/adventure-map" className="nav-link">
          üó∫Ô∏è Map
        </NavLink>
        <NavLink to="/chatbot" className="nav-link">
          üß† Chatbot
        </NavLink>
        <NavLink to="/leaderboard" className="nav-link">
          üèÜ Rank
        </NavLink>

        {!isLoggedIn ? (
          <NavLink to="/login" className="nav-link">
            üîê Login
          </NavLink>
        ) : (
          <NavLink to="/logout" className="nav-link">
            <span style={{ cursor: "pointer" }}>üö™ Logout</span>
          </NavLink>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
