import React, { useEffect, useState } from "react";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch("/user/stats");
        if (!response.ok) {
          throw new Error("Failed to fetch user stats");
        }
        const data = await response.json();
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <div className="profile-card">Loading...</div>;
  if (error) return <div className="profile-card">Error: {error}</div>;

  return (
    <div className="profile-card">
      <h2 className="profile-title">{user.user_id}'s Fitness Profile</h2>

      <div className="profile-item">
        <label>Level:</label>
        <span>{user.level}</span>
      </div>

      <div className="profile-item">
        <label>XP:</label>
        <span>
          {user.xp} / {user.xpGoal}
        </span>
        <div className="progress-bar">
          <div
            className="progress-fill"
            style={{ width: `${(user.xp / user.xpGoal) * 100}%` }}
          ></div>
        </div>
      </div>

      <div className="profile-item">
        <label>Score:</label>
        <span>{user.score}</span>
      </div>

      <div className="profile-item">
        <label>Exercises Completed:</label>
        <span>{user.exercises}</span>
      </div>

      <div className="profile-item">
        <label>Total Reps:</label>
        <span>{user.reps}</span>
      </div>

      <div className="profile-item">
        <label>Goals Achieved:</label>
        <span>{user.goals}</span>
      </div>
    </div>
  );
};

export default Profile;
