import apiFetch from "./api";

const workoutService = {
  startLiveDetection: (exercise) => {
    const user_id = localStorage.getItem("email");
    if (!user_id) throw new Error("User not logged in or missing user_id");

    return apiFetch("/start", {
      method: "POST",
      body: {
        user_id,
        type: exercise,
      },
    });
  },

  uploadVideoDetection: (videoFile, exercise) => {
    const formData = new FormData();
    formData.append("video", videoFile);
    formData.append("exercise", exercise);

    return apiFetch("/upload", {
      method: "POST",
      body: formData,
      isForm: true,
    });
  },

  logWorkout: (data) =>
    apiFetch("/workout/log", {
      method: "POST",
      body: data,
    }),

  getTodayProgress: () => {
    const user_id = localStorage.getItem("email");
    return apiFetch("/workout/progress", {
      method: "POST",
      body: { user_id },
    });
  },
};

export default workoutService;
