// src/pages/ExerciseSelector.js
import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Button from "../../components/Button";

const ExerciseSelector = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [exercise, setExercise] = useState(location.state?.exercise || "jump");
  const [mode, setMode] = useState("webcam");
  const [videoFile, setVideoFile] = useState(null);

  const handleStart = () => {
    const email = localStorage.getItem("email"); // ✅ Get email from localStorage

    if (!email) {
      alert("User not logged in.");
      return;
    }

    if (mode === "upload" && !videoFile) {
      alert("Please upload a video.");
      return;
    }

    navigate("/live-workout", {
      state: {
        exercise,
        mode,
        videoFile,
        level: location.state?.level?.name || "",
        email, // ✅ Pass email to LiveWorkout
      },
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setVideoFile(file);

    const reader = new FileReader();
    reader.onload = () => {
      localStorage.setItem("uploadedWorkoutVideo", reader.result);
    };
    if (file) reader.readAsDataURL(file);
  };

  const styles = {
    container: {
      padding: "16px",
      maxWidth: "400px",
      margin: "0 auto",
      textAlign: "center",
    },
    heading: { fontSize: "24px", fontWeight: "700", marginBottom: "16px" },
    label: { display: "block", marginBottom: "8px", textAlign: "left" },
    select: { width: "100%", padding: "8px", marginBottom: "16px" },
    radioGroup: {
      display: "flex",
      justifyContent: "space-around",
      marginBottom: "16px",
    },
    fileInput: { marginBottom: "16px", width: "100%" },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Choose Your Workout</h2>

      <label style={styles.label}>Exercise Type:</label>
      <select
        value={exercise}
        onChange={(e) => setExercise(e.target.value)}
        style={styles.select}
        disabled={!!location.state?.exercise}
      >
        <option value="jump">Jump</option>
        <option value="squat">Squat</option>
        <option value="pushup">Push-up</option>
        <option value="plank">Plank</option>
      </select>

      <label style={styles.label}>Mode:</label>
      <div style={styles.radioGroup}>
        <label>
          <input
            type="radio"
            value="webcam"
            checked={mode === "webcam"}
            onChange={(e) => setMode(e.target.value)}
          />
          Webcam
        </label>
        <label>
          <input
            type="radio"
            value="upload"
            checked={mode === "upload"}
            onChange={(e) => setMode(e.target.value)}
          />
          Upload Video
        </label>
      </div>

      {mode === "upload" && (
        <input
          type="file"
          accept="video/*"
          onChange={handleFileChange}
          style={styles.fileInput}
        />
      )}

      <Button onClick={handleStart} text="Start Workout" />
    </div>
  );
};

export default ExerciseSelector;
