import React, { useState, useEffect, useRef } from "react";
import foodDB from "./data/foodDB.json";
import vegFoodDB from "./data/vegetarianFoodDB.json";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    {
      from: "bot",
      text: "Hi! What did you eat today? Iâ€™ll help track your nutrition.",
    },
  ]);
  const [input, setInput] = useState("");
  const [foodLog, setFoodLog] = useState([]);
  const [dietType, setDietType] = useState("non-veg"); // new toggle
  const chatEndRef = useRef(null);

  const userDetails = {
    age: 25,
    weight: 70,
    height: 170,
    goal: "weight_loss",
  };

  const calculateUserGoal = ({ age, weight, height, goal }) => {
    const bmr = 10 * weight + 6.25 * height - 5 * age + 5;
    let calories = bmr;
    if (goal === "weight_gain") calories += 300;
    else if (goal === "weight_loss") calories -= 500;

    return {
      calories: Math.round(calories),
      protein: Math.round(weight * 1.6),
      carbs: Math.round(weight * 2.5),
      fat: Math.round(weight * 1),
    };
  };

  const userGoal = calculateUserGoal(userDetails);

  const handleSend = () => {
    if (!input.trim()) return;
    const userMessage = { from: "user", text: input };
    setMessages((msgs) => [...msgs, userMessage]);
    setInput("");

    setTimeout(() => {
      const response = getBotResponse(input);
      setMessages((msgs) => [...msgs, { from: "bot", text: response }]);
    }, 600);
  };

  const getBotResponse = (input) => {
    const db = dietType === "veg" ? vegFoodDB : foodDB;
    const items = input
      .toLowerCase()
      .split(/,|\band\b|\n/)
      .map((i) => i.trim());
    let found = [];
    let total = { calories: 0, protein: 0, carbs: 0, fat: 0 };

    items.forEach((item) => {
      for (let key in db) {
        if (item.includes(key)) {
          found.push(key);
          const data = db[key];
          total.calories += data.calories;
          total.protein += data.protein;
          total.carbs += data.carbs;
          total.fat += data.fat;
        }
      }
    });

    if (found.length === 0)
      return "â— I couldn't recognize any food items. Try common items like rice, dosa, banana.";

    setFoodLog((prev) => [...prev, ...found]);

    const deficit = {
      calories: userGoal.calories - total.calories,
      protein: userGoal.protein - total.protein,
      carbs: userGoal.carbs - total.carbs,
      fat: userGoal.fat - total.fat,
    };

    let suggestion = "";
    if (deficit.protein > 20)
      suggestion += "\n\nðŸŸ¡ Youâ€™re low on protein. Try paneer, dal, or nuts.";
    if (deficit.calories > 300)
      suggestion +=
        "\n\nðŸŸ¡ Add calorie-rich foods like bananas, rice, smoothies.";
    if (deficit.carbs > 50)
      suggestion +=
        "\n\nðŸŸ¡ Need more carbs? Include dosa, idli, puttu, or fruits.";

    return (
      `âœ… Here's your macro breakdown:\n` +
      `Calories: ${total.calories.toFixed(0)} kcal\n` +
      `Protein: ${total.protein.toFixed(1)}g\n` +
      `Carbs: ${total.carbs.toFixed(1)}g\n` +
      `Fat: ${total.fat.toFixed(1)}g` +
      suggestion +
      "\n\nðŸ½ï¸ Let me know what else you ate!"
    );
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div style={styles.wrapper}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸ¥— Nutrition Chatbot</h2>

        <div style={{ marginBottom: "10px", textAlign: "center" }}>
          <label htmlFor="dietSelect">Diet Type: </label>
          <select
            id="dietSelect"
            value={dietType}
            onChange={(e) => setDietType(e.target.value)}
            style={{ padding: "5px 10px", borderRadius: "6px" }}
          >
            <option value="veg">Vegetarian</option>
            <option value="non-veg">Non-Vegetarian</option>
          </select>
        </div>

        <div style={styles.chatWindow}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                ...styles.message,
                alignSelf: msg.from === "user" ? "flex-end" : "flex-start",
                backgroundColor: msg.from === "user" ? "#007bff" : "#e0e0e0",
                color: msg.from === "user" ? "#fff" : "#000",
              }}
            >
              {msg.text}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <div style={styles.inputArea}>
          <input
            type="text"
            placeholder="E.g. idli, rice and sambar..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            style={styles.input}
          />
          <button onClick={handleSend} style={styles.sendButton}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginTop: "50px",
    padding: "20px",
  },
  card: {
    backgroundColor: "#fff",
    padding: "24px",
    borderRadius: "16px",
    boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
    width: "100%",
    maxWidth: "500px",
    display: "flex",
    flexDirection: "column",
    height: "80vh",
  },
  heading: {
    textAlign: "center",
    marginBottom: "16px",
    fontSize: "22px",
    color: "#333",
  },
  chatWindow: {
    flex: 1,
    padding: "10px",
    border: "1px solid #ddd",
    borderRadius: "12px",
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    backgroundColor: "#fafafa",
    marginBottom: "12px",
  },
  message: {
    maxWidth: "75%",
    padding: "10px 14px",
    borderRadius: "20px",
    fontSize: "15px",
    lineHeight: "1.4",
  },
  inputArea: {
    display: "flex",
    gap: "10px",
  },
  input: {
    flex: 1,
    padding: "10px 12px",
    borderRadius: "10px",
    border: "1px solid #ccc",
    fontSize: "15px",
  },
  sendButton: {
    padding: "10px 16px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontWeight: "bold",
  },
};

export default Chatbot;
