// src/components/PoseFeedback.js

import React from "react";

const PoseFeedback = ({ accuracy }) => {
  let feedbackText = "Keep trying!";
  let feedbackColor = "#f56565"; // red

  if (accuracy >= 90) {
    feedbackText = "Excellent form! ðŸ’ª";
    feedbackColor = "#48bb78"; // green
  } else if (accuracy >= 70) {
    feedbackText = "Good job, keep improving!";
    feedbackColor = "#ed8936"; // orange
  }

  return (
    <div
      style={{
        padding: "12px 16px",
        borderRadius: "8px",
        backgroundColor: feedbackColor,
        color: "white",
        fontWeight: "600",
        fontSize: "1.1rem",
        maxWidth: "400px",
        margin: "20px auto",
        textAlign: "center",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
      }}
    >
      {accuracy !== undefined
        ? `Pose Accuracy: ${accuracy.toFixed(2)}% â€” ${feedbackText}`
        : "No feedback available."}
    </div>
  );
};

export default PoseFeedback;
