import React, { useEffect, useState } from "react";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [playedDates, setPlayedDates] = useState([]);
  const [flipped, setFlipped] = useState(false);

  const [streak, setStreak] = useState(3); // Example streak
  const [weekProgress, setWeekProgress] = useState([
    "✅",
    "✅",
    "",
    "",
    "",
    "",
    "",
  ]); // Example

  useEffect(() => {
    const fetchStats = async () => {
      const userId = localStorage.getItem("user_id");
      if (!userId) {
        setError("User ID not found");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(
          `http://localhost:5001/user/stats?user_id=${userId}`
        );
        if (!response.ok) throw new Error("Failed to fetch user stats");
        const data = await response.json();
        console.log(data);
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const handleStartWorkout = () => {
    alert("Workout started!");
  };

  if (loading) return <div className="dashboard">Loading...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;

  const renderCalendar = () => {
    const today = new Date();
    const year = today.getFullYear();
    const month = today.getMonth(); // 0-indexed

    const firstDay = new Date(year, month, 1);
    const daysInMonth = new Date(year, month + 1, 0).getDate();

    const dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    const calendarDays = [];

    for (let i = 0; i < firstDay.getDay(); i++) {
      calendarDays.push(null);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      calendarDays.push(day);
    }

    const isPlayed = (day) => {
      if (!day) return false;
      const dateStr = `${year}-${String(month + 1).padStart(2, "0")}-${String(
        day
      ).padStart(2, "0")}`;
      return playedDates.includes(dateStr);
    };

    return (
      <div>
        <div className="calendar-grid">
          {dayNames.map((name) => (
            <div key={name} className="day-name">
              {name}
            </div>
          ))}
          {calendarDays.map((day, idx) => (
            <div
              key={idx}
              className={`day-cell ${isPlayed(day) ? "played" : ""} ${
                day === today.getDate() ? "today" : ""
              }`}
            >
              {day || ""}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard">
      {/* Left Card: Profile */}
      <div className="left-section">
        <div className={`card flip-card ${flipped ? "flipped" : ""}`}>
          <div className="flip-card-inner">
            {/* Front: Profile Info */}
            <div className="flip-card-front">
              <div className="profile-image"></div>
              <h3>{user.name || user.user_id}</h3>
              <p>{user.name}</p>
              <button className="save-btn" onClick={() => setFlipped(true)}>
                View Achievements
              </button>
            </div>

            {/* Back: Achievements */}
            <div className="flip-card-back">
              <h3>🏅 Achievements</h3>
              <ul className="badge-list">
                <li>🔥 Burned 1000 Kcal</li>
                <li>💪 7-Day Workout Streak</li>
                <li>🎯 Goal Crushed</li>
              </ul>
              <button className="save-btn" onClick={() => setFlipped(false)}>
                Back to Profile
              </button>
            </div>
          </div>
        </div>

        <div className="card calorie-card">
          <h4>🔥 Calories Burned</h4>
          <p className="calorie-value">{user.calories_burned || 0} kcal</p>
        </div>
      </div>
      {/* Right Container with two stacked cards */}
      <div className="right-cards">
        {/* Top-right: Fitness Stats */}
        <div className="card fitness-stats">
          <h4>Fitness Stats</h4>
          <div className="stat">
            <label>Level:</label> <span>{user.level || "1"}</span>
          </div>
          <div className="stat">
            <label>XP:</label>
            <span>{user.total_xp || 200} / 1000</span>
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{ width: `${((user.total_xp || 0) / 1000) * 100}%` }}
              />
            </div>
          </div>
          <div className="stat">
            <label>Score:</label> <span>{user.total_score || 1000}</span>
          </div>
          <div className="stat">
            <label>Exercises:</label>{" "}
            <span>{user.workouts_completed || 5}</span>
          </div>
          <div className="stat">
            <label>Reps:</label> <span>{user.total_reps || 100}</span>
          </div>
          <div className="stat">
            <label>Goals:</label> <span>{user.goals || "2000"}</span>
          </div>
        </div>

        {/* Bottom-right: Streak + Calendar */}
        <div className="card streak-calendar">
          <h4 className="month-heading">🗓️ This Month</h4>
          <div className="full-calender">{renderCalendar()}</div>

          {/* <button className="start-button" onClick={handleStartWorkout}>
            Start Workout
          </button> */}
        </div>
      </div>
    </div>
  );
};

export default Profile;
