import React, { useEffect, useState } from "react";
import Confetti from "react-confetti";
import "./CelebrationModal.css";

const CelebrationModal = ({ result, onClose, onRetry, onNext }) => {
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <>
      <Confetti
        width={windowDimensions.width}
        height={windowDimensions.height}
        numberOfPieces={300}
        recycle={false}
      />

      <div className="modal-overlay">
        <div className="modal-content">
          <h2>ðŸŽ‰ Level Completed!</h2>
          <p>
            <strong>Feedback:</strong> {result?.feedback}
          </p>
          <p>
            <strong>XP:</strong> {result?.xp}
          </p>
          <p>
            <strong>Score:</strong> {result?.score}
          </p>
          <p>
            <strong>Accuracy:</strong> {result?.accuracy}%
          </p>
          <p>
            <strong>Calories:</strong> {result?.calories} kcal
          </p>
          <p>
            <strong>Reps:</strong> {result?.reps}
          </p>

          <div className="button-group">
            <button className="quit-btn" onClick={onClose}>
              Quit
            </button>
            <button className="retry-btn" onClick={onRetry}>
              Retry
            </button>
            <button className="next-btn" onClick={onNext}>
              Next
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default CelebrationModal;
