import React from "react";
import "./XPProgressCard.css";

const XPProgressCard = ({ level, xp, maxXp }) => {
  const progress = Math.min((xp / maxXp) * 100, 100).toFixed(1);

  const getBadgeInfo = () => {
    if (level >= 10) return { icon: "ðŸ”¥", label: "Master" };
    if (level >= 5) return { icon: "ðŸ¥ˆ", label: "Intermediate" };
    return { icon: "ðŸŽ¯", label: "Beginner" };
  };

  const badge = getBadgeInfo();

  return (
    <div className="xp-card">
      <div className="badge-header">
        <span className="badge-icon">{badge.icon}</span>
        <span className="badge-title">{badge.label}</span>
      </div>
      <p>
        <strong>Level:</strong> {level}
      </p>
      <p>
        <strong>XP:</strong> {xp} / {maxXp}
      </p>
      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }} />
      </div>
    </div>
  );
};

export default XPProgressCard;
