import React, { useEffect, useState } from "react";
import "./Leaderboard.css"; // Responsive CSS

const getBadge = (rank, user) => {
  if (rank === 0) return "üèÜ Champion";
  if (rank === 1) return "ü•á Top Performer";
  if (rank === 2) return "‚ö°Ô∏è Hard Charger";
  if (user.xp >= 1000 && user.xp < 4000) return "üéØ XP Grinder";
  if (user.xp >= 4000) return "üî• Streak Master";
  return "üéñÔ∏è Member";
};

const Leaderboard = () => {
  const userId = localStorage.getItem("user_id");

  const [leaderboardData, setLeaderboardData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // You can modify this to dynamically use userId or fetch all users
  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/user/stats?user_id=${userId}`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch leaderboard data");
        }
        const data = await response.json();
        console.log("Leaderboard data:", data);
        setLeaderboardData(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  return (
    <div className="leaderboard-wrapper">
      <div className="leaderboard-card">
        <h2 className="leaderboard-heading">üèÖ Leaderboard</h2>

        {loading && <p>Loading...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {!loading && !error && (
          <div className="table-container">
            <table className="leaderboard-table">
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Name</th>
                  <th>Badge</th>
                  <th>Level</th>
                  <th>XP</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboardData.map((user, idx) => (
                  <tr key={user.id}>
                    <td>{idx + 1}</td>
                    <td>{user.name}</td>
                    <td>{getBadge(idx, user)}</td>
                    <td>Lv. {user.level}</td>
                    <td>{user.xp} XP</td>
                    <td>{user.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Leaderboard;
