import React from "react";

const sampleLeaderboard = [
  {
    id: 1,
    name: "Alice",
    score: 1200,
    level: 10,
    xp: 4500,
    badge: "üèÜ",
  },
  {
    id: 2,
    name: "Bob",
    score: 1100,
    level: 9,
    xp: 3900,
    badge: "ü•à",
  },
  {
    id: 3,
    name: "Charlie",
    score: 900,
    level: 8,
    xp: 3200,
    badge: "ü•â",
  },
];

const Leaderboard = () => {
  return (
    <div style={styles.wrapper}>
      <div style={styles.card}>
        <h2 style={styles.heading}>üèÖ Leaderboard</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Rank</th>
              <th style={styles.th}>Name</th>
              <th style={styles.th}>Badge</th>
              <th style={styles.th}>Level</th>
              <th style={styles.th}>XP</th>
              <th style={styles.th}>Score</th>
            </tr>
          </thead>
          <tbody>
            {sampleLeaderboard.map((user, idx) => (
              <tr key={user.id} style={styles.tr}>
                <td style={styles.td}>{idx + 1}</td>
                <td style={styles.td}>{user.name}</td>
                <td style={styles.td}>{user.badge}</td>
                <td style={styles.td}>Lv. {user.level}</td>
                <td style={styles.td}>{user.xp} XP</td>
                <td style={styles.td}>{user.score}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    display: "flex",
    justifyContent: "center",
    padding: "40px",
    // Light gray background for contrast
  },
  card: {
    width: "100%",
    maxWidth: "900px",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 6px 18px rgba(0, 0, 0, 0.1)",
    padding: "24px",
  },
  heading: {
    fontSize: "28px",
    textAlign: "center",
    marginBottom: "24px",
    color: "#333",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  th: {
    textAlign: "left",
    padding: "12px",
    borderBottom: "2px solid #e0e0e0",
    fontWeight: "600",
    color: "#555",
  },
  tr: {
    borderBottom: "1px solid #eee",
  },
  td: {
    padding: "12px",
    color: "#333",
  },
};

export default Leaderboard;
