// src/pages/LiveWorkout/LiveWorkout.js

import React, { useEffect, useState, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { workoutService } from "../../services/workoutService";
import { WorkoutProgressContext } from "../../contexts/WorkoutProgressContext";
import PoseFeedback from "../../components/PoseFeedback";
import ExerciseAvatar3D from "../../components/ExerciseAvatar3D";

const LiveWorkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { updateProgress } = useContext(WorkoutProgressContext);

  const { exercise, mode } = location.state || {};
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!exercise || !mode) {
      navigate("/dailychallenge");
      return;
    }

    const startWorkout = async () => {
      try {
        let res;
        if (mode === "webcam") {
          res = await workoutService.startLiveDetection(exercise);
        } else {
          const file = localStorage.getItem("uploadedWorkoutVideo");
          if (!file) throw new Error("No video uploaded");
          res = await workoutService.uploadWorkoutVideo(file, exercise);
        }

        const { output, score, accuracy, xp } = res;
        setResult({ output, score, accuracy, xp });
        updateProgress({ exercise, score, xp, completed: true });
      } catch (error) {
        console.error(error);
        setResult({ error: "Workout failed. Try again." });
      } finally {
        setIsLoading(false);
      }
    };

    startWorkout();
  }, [exercise, mode, navigate, updateProgress]);

  if (isLoading)
    return <div className="text-center mt-10">Starting workout...</div>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">
        {exercise.toUpperCase()} - Results
      </h2>

      {result?.error ? (
        <div className="text-red-500">{result.error}</div>
      ) : (
        <>
          <PoseFeedback accuracy={result.accuracy} />
          <ExerciseAvatar3D exercise={exercise} />
          <p className="mt-4">
            XP Earned: <strong>{result.xp}</strong>
          </p>
          <p>
            Score: <strong>{result.score}</strong>
          </p>
        </>
      )}

      <button
        onClick={() => navigate("/dailychallenge")}
        className="mt-6 bg-blue-500 text-white px-4 py-2 rounded"
      >
        Back to Challenges
      </button>
    </div>
  );
};

export default LiveWorkout;
