import React from "react";
import { useNavigate } from "react-router-dom";
import "./AdventureMap.css";

const levels = [
  { id: 1, name: "Forest Jump Lv1", description: "Jump 10 times" },
  { id: 2, name: "Mountain Squat Lv2", description: "Squat 15 times" },
  { id: 3, name: "Cave Pushup Lv3", description: "Pushup 10 times" },
];

const AdventureMap = () => {
  const navigate = useNavigate();

  const handleLevelSelect = (level) => {
    // Pass level and exercise data to ExerciseSelector
    navigate("/select-workout", {
      state: {
        levelId: level.id,
        levelName: level.name,
        exercise: level.name.toLowerCase().includes("jump")
          ? "jump"
          : level.name.toLowerCase().includes("squat")
          ? "squat"
          : level.name.toLowerCase().includes("pushup")
          ? "pushup"
          : "", // fallback if not matched
      },
    });
  };

  return (
    <div className="map-container">
      <h2 className="map-title">Choose Your Adventure</h2>
      <div className="level-list">
        {levels.map((level) => (
          <div
            key={level.id}
            className="level-card"
            onClick={() => handleLevelSelect(level)}
          >
            <h3>{level.name}</h3>
            <p>{level.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdventureMap;
