// src/context/WorkoutProgressContext.js

import React, { createContext, useContext, useEffect, useState } from "react";
import {
  saveWorkoutResult,
  getTodayProgress,
} from "../services/workoutService";

const WorkoutProgressContext = createContext();

export const useWorkoutProgress = () => useContext(WorkoutProgressContext);

export const WorkoutProgressProvider = ({ children }) => {
  const [dailyProgress, setDailyProgress] = useState({
    date: "",
    completed: false,
    xp: 0,
    penalties: 0,
    workoutType: "",
    accuracy: 0,
  });

  const fetchToday = async () => {
    const res = await getTodayProgress();
    if (res?.success) setDailyProgress(res.data);
  };

  useEffect(() => {
    fetchToday();
  }, []);

  const updateWorkoutResult = async ({ xp, accuracy, workoutType }) => {
    const res = await saveWorkoutResult({ xp, accuracy, workoutType });
    if (res?.success) {
      setDailyProgress((prev) => ({
        ...prev,
        completed: true,
        xp,
        accuracy,
        workoutType,
      }));
    }
  };

  return (
    <WorkoutProgressContext.Provider
      value={{ dailyProgress, updateWorkoutResult }}
    >
      {children}
    </WorkoutProgressContext.Provider>
  );
};
