import React, { useEffect, useState } from "react";
import "./Profile.css";
import assassinImg from "../assets/images/assassincreed.png";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [streak, setStreak] = useState(3); // Example streak
  const [weekProgress, setWeekProgress] = useState([
    "‚úÖ",
    "‚úÖ",
    "",
    "",
    "",
    "",
    "",
  ]); // Example

  useEffect(() => {
    const fetchStats = async () => {
      const userId = localStorage.getItem("user_id");
      if (!userId) {
        setError("User ID not found");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(
          `http://localhost:5001/user/stats?user_id=${userId}`
        );
        if (!response.ok) throw new Error("Failed to fetch user stats");
        const data = await response.json();
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const handleStartWorkout = () => {
    // Logic to start workout
    alert("Workout started!");
  };

  if (loading) return <div className="dashboard">Loading...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;

  return (
    <div className="dashboard">
      {/* Profile Card */}
      <div className="card profile-section">
        <img
          src="assets/images/assassincreed.png"
          alt="Assassin Creed"
          className="profile-pic"
        />
        <h3>{user.username || user.user_id}</h3>
        <p>{user.email}</p>
        <button className="save-btn">Save</button>
      </div>

      {/* Fitness Stats Card */}
      <div className="card fitness-stats">
        <h4>Fitness Stats</h4>
        <div className="stat">
          <label>Level:</label> <span>{user.level}</span>
        </div>
        <div className="stat">
          <label>XP:</label>
          <span>
            {user.xp} / {user.xpGoal}
          </span>
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{ width: `${(user.xp / user.xpGoal) * 100}%` }}
            />
          </div>
        </div>
        <div className="stat">
          <label>Score:</label> <span>{user.score}</span>
        </div>
        <div className="stat">
          <label>Exercises:</label> <span>{user.exercises}</span>
        </div>
        <div className="stat">
          <label>Reps:</label> <span>{user.reps}</span>
        </div>
        <div className="stat">
          <label>Goals:</label> <span>{user.goals}</span>
        </div>
      </div>

      {/* Streak Tracker */}
      <div className="card streak-tracker">
        <h4>üî• Streak: {streak} Days</h4>
        <p className="hint">
          Keep it up! Reach <span className="highlight">5 Days</span> to earn a
          reward.
        </p>
      </div>

      {/* Calendar & Workout */}
      <div className="card calendar-tracker">
        <h4>üóìÔ∏è This Week</h4>
        <div className="calendar">
          {["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"].map((day, i) => (
            <div
              key={day}
              className={`day-box ${weekProgress[i] === "‚úÖ" ? "active" : ""}`}
            >
              {day} {weekProgress[i]}
            </div>
          ))}
        </div>
        <button className="start-button" onClick={handleStartWorkout}>
          Start Workout
        </button>
      </div>
    </div>
  );
};

export default Profile;
