// Let's start with the first step: user registration of height, weight, and fitness goal.

// === STEP 1: RegisterUserData.js ===

// File: src/pages/Setup/RegisterUserData.js
import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";

const RegisterUserData = () => {
  const { setUserData } = useContext(UserDataContext);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    height: "",
    weight: "",
    goal: "maintain",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setUserData(formData);
    navigate("/dailychallenge");
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Set Up Your Fitness Profile</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label>Height (cm)</label>
          <input
            type="number"
            name="height"
            value={formData.height}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>
        <div>
          <label>Weight (kg)</label>
          <input
            type="number"
            name="weight"
            value={formData.weight}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>
        <div>
          <label>Goal</label>
          <select
            name="goal"
            value={formData.goal}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          >
            <option value="lose">Lose Weight</option>
            <option value="gain">Gain Weight</option>
            <option value="maintain">Maintain</option>
          </select>
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Continue
        </button>
      </form>
    </div>
  );
};

export default RegisterUserData;
