import axios from "./api";

export const getTodayProgress = async () => {
  const response = await axios.get("/workout/today");
  return response.data;
};

export const saveWorkoutResult = async ({ xp, accuracy, workoutType }) => {
  const response = await axios.post("/workout/save", {
    xp,
    accuracy,
    workoutType,
  });
  return response.data;
};

export const startLiveDetection = async (exercise) => {
  const response = await axios.post("/workout/live", { exercise });
  return response.data;
};

export const uploadVideoDetection = async (exercise, videoFile) => {
  const formData = new FormData();
  formData.append("exercise", exercise);
  formData.append("video", videoFile);

  const response = await axios.post("/workout/upload", formData, {
    headers: {
      "Content-Type": "multipart/form-data",
    },
  });
  return response.data;
};

export const logWorkout = async (data) => {
  const response = await axios.post("/workout/log", data);
  return response.data;
};
