import React from "react";
import "./AdventureMap.css"; // Custom CSS for styling
import { Link } from "react-router-dom";

const levels = [
  {
    id: 1,
    name: "Forest Jump",
    status: "completed",
    icon: "üå≤",
    combos: [
      {
        id: "1-1",
        name: "Forest Trial I",
        exercises: [
          { type: "jump", reps: 10 },
          { type: "pushup", reps: 5 },
        ],
        status: "completed",
      },
      {
        id: "1-2",
        name: "Forest Trial II",
        exercises: [
          { type: "jump", reps: 20 },
          { type: "pushup", reps: 10 },
          { type: "squat", reps: 10 },
        ],
        status: "unlocked",
      },
      {
        id: "1-3",
        name: "Forest Trial III",
        exercises: [
          { type: "jump", reps: 40 },
          { type: "pushup", reps: 20 },
          { type: "plank", time: "30s" },
        ],
        status: "locked",
      },
    ],
  },
  {
    id: 2,
    name: "Mountain Push-Up",
    task: "Do 10 Push-Ups",
    status: "unlocked",
    icon: "‚õ∞Ô∏è",
  },
  {
    id: 3,
    name: "Desert Squat",
    task: "15 Squats",
    status: "locked",
    icon: "üèúÔ∏è",
  },
  {
    id: 4,
    name: "Icy Plank",
    task: "30s Plank",
    status: "locked",
    icon: "‚ùÑÔ∏è",
  },
  {
    id: 5,
    name: "Crystal Temple",
    task: "Combo Workout",
    status: "locked",
    icon: "üèØ",
  },
];

function AdventureMap() {
  return (
    <div className="adventure-map-container">
      <h1 className="title">üèÅ Adventure Fitness Map</h1>
      <div className="map-path">
        {levels.map((level) => (
          <div key={level.id} className={`map-node ${level.status}`}>
            <div className="icon">{level.icon}</div>
            <div className="details">
              <h3>{level.name}</h3>
              <div className="combo-levels">
                {level.combos?.map((combo) => (
                  <div key={combo.id} className={`combo-node ${combo.status}`}>
                    <h4>{combo.name}</h4>
                    <ul>
                      {combo.exercises.map((ex, i) => (
                        <li key={i}>
                          {ex.type} ‚Äì {ex.reps || ex.time}
                        </li>
                      ))}
                    </ul>
                    {combo.status !== "locked" ? (
                      <Link
                        to={`/liveworkout?combo=${combo.id}`}
                        className="start-combo-btn"
                      >
                        Start
                      </Link>
                    ) : (
                      <button className="locked-btn" disabled>
                        üîí Locked
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdventureMap;
