// src/components/ExerciseAvatar3D.js

import React from "react";

const exerciseIcons = {
  jump: "ü§∏‚Äç‚ôÇÔ∏è",
  squat: "üèãÔ∏è‚Äç‚ôÄÔ∏è",
  pushup: "üí™",
  plank: "üßò‚Äç‚ôÇÔ∏è",
};

const ExerciseAvatar3D = ({ exercise }) => {
  return (
    <div
      style={{
        margin: "20px auto",
        width: "250px",
        height: "250px",
        borderRadius: "20px",
        backgroundColor: "#e2e8f0", // light gray-blue
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        fontSize: "6rem",
        color: "#2d3748", // dark gray
        boxShadow: "0 8px 15px rgba(0,0,0,0.1)",
        userSelect: "none",
      }}
    >
      <span role="img" aria-label={exercise}>
        {exerciseIcons[exercise] || "üèÉ‚Äç‚ôÇÔ∏è"}
      </span>
      <p
        style={{
          marginTop: "16px",
          fontSize: "1.5rem",
          fontWeight: "700",
          textTransform: "capitalize",
          color: "#4a5568",
        }}
      >
        {exercise || "Exercise"}
      </p>
    </div>
  );
};

export default ExerciseAvatar3D;
