import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [playedDates, setPlayedDates] = useState([]);
  const [flipped, setFlipped] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [flippedDate, setFlippedDate] = useState(null); // stores flipped day (date string)
  const [activitiesByDate, setActivitiesByDate] = useState({}); // map date -> activities array

  const [streak, setStreak] = useState(3); // Example streak
  const [weekProgress, setWeekProgress] = useState([
    "✅",
    "✅",
    "",
    "",
    "",
    "",
    "",
  ]); // Example

  useEffect(() => {
    const fetchStats = async () => {
      const userId = localStorage.getItem("user_id");
      if (!userId) {
        setError("User ID not found");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(
          `http://localhost:5001/user/stats?user_id=${userId}`
        );
        if (!response.ok) throw new Error("Failed to fetch user stats");
        const data = await response.json();
        console.log(data);
        setUser(data);
        if (data.played_dates) {
          console.log("Played Dates:", data.played_dates);
          setPlayedDates(data.played_dates);
        }
        if (data.activities) {
          setActivitiesByDate(data.activities);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <div className="dashboard">Loading...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;
  const handlePrevMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
  };

  const handleNextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
  };
  const renderCalendar = () => {
    const firstDay = new Date(currentYear, currentMonth, 1);
    const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

    const dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    const calendarDays = [];

    for (let i = 0; i < firstDay.getDay(); i++) {
      calendarDays.push(null);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      calendarDays.push(day);
    }

    const isPlayed = (day) => {
      if (!day) return false;
      const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(
        2,
        "0"
      )}-${String(day).padStart(2, "0")}`;
      return playedDates.includes(dateStr);
    };

    return (
      <div>
        <div className="calendar-container">
          <a className="side-arrow left" onClick={handlePrevMonth}>
            &lt;
          </a>

          <div className="calendar-grid-wrapper">
            <div className="calendar-header">
              <h4 className="month-label">
                {new Date(currentYear, currentMonth).toLocaleString("default", {
                  month: "long",
                  year: "numeric",
                })}
              </h4>
            </div>

            <div className="calendar-grid">
              {dayNames.map((name) => (
                <div key={name} className="day-name">
                  {name}
                </div>
              ))}
              {calendarDays.map((day, idx) => (
                <div
                  key={idx}
                  className={`day-cell ${isPlayed(day) ? "played" : ""} ${
                    day === new Date().getDate() &&
                    currentMonth === new Date().getMonth() &&
                    currentYear === new Date().getFullYear()
                      ? "today"
                      : ""
                  }`}
                >
                  {day || ""}
                </div>
              ))}
            </div>
          </div>

          <a className="side-arrow right" onClick={handleNextMonth}>
            &gt;
          </a>
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard">
      {/* Left Card: Profile */}
      <div className="left-section">
        <div className={`card flip-card ${flipped ? "flipped" : ""}`}>
          <div className="flip-card-inner">
            {/* Front: Profile Info */}
            <div className="flip-card-front">
              <div className="profile-image"></div>
              <div className="profile-info">
                <div className="profile-row">
                  <span className="profile-label">Name:</span>
                  <span className="profile-value">{user.name}</span>
                </div>
                <div className="profile-row">
                  <span className="profile-label">Age:</span>
                  <span className="profile-value">{user.age}</span>
                </div>
                <div className="profile-row">
                  <span className="profile-label">Gender:</span>
                  <span className="profile-value">{user.gender}</span>
                </div>
                <div className="profile-row">
                  <span className="profile-label">Height:</span>
                  <span className="profile-value">{user.height} cm</span>
                </div>
                <div className="profile-row">
                  <span className="profile-label">Weight:</span>
                  <span className="profile-value">{user.weight} kg</span>
                </div>
              </div>
              <button className="save-btn" onClick={() => setFlipped(true)}>
                View Achievements
              </button>
            </div>

            {/* Back: Achievements */}
            <div className="flip-card-back">
              <h3>🏅 Achievements</h3>
              <ul className="badge-list">
                <li>🔥 Burned 1000 Kcal</li>
                <li>💪 7-Day Workout Streak</li>
                <li>🎯 Goal Crushed</li>
              </ul>
              <button className="save-btn" onClick={() => setFlipped(false)}>
                Back to Profile
              </button>
            </div>
          </div>
        </div>

        <div className="card calorie-card">
          <h4>🔥 Calories Burned</h4>
          <p className="calorie-value">{user.calories || 0} cal</p>
        </div>
        <Link to="/daily-challenge">
          <button className="startbutton">Start Workout</button>
        </Link>
      </div>
      {/* Right Container with two stacked cards */}
      <div className="right-cards">
        {/* Top-right: Fitness Stats */}
        <div className="card fitness-stats">
          <h4>Fitness Stats</h4>
          <div className="stat">
            <label>Level:</label> <span>{user.level || "1"}</span>
          </div>
          <div className="stat">
            <label>XP:</label>
            <span>{user.total_xp || 200} / 1000</span>
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{ width: `${((user.total_xp || 0) / 1000) * 100}%` }}
              />
            </div>
          </div>
          <div className="stat">
            <label>Score:</label> <span>{user.total_score || 1000}</span>
          </div>
          <div className="stat">
            <label>Exercises:</label>{" "}
            <span>{user.workouts_completed || 5}</span>
          </div>
          <div className="stat">
            <label>Reps:</label> <span>{user.total_reps || 100}</span>
          </div>
          <div className="stat">
            <label>Goals:</label> <span>{user.goals || "2000"}</span>
          </div>
        </div>

        {/* Bottom-right: Streak + Calendar */}
        <div className="card streak-calendar">
          <h4 className="month-heading">🗓️ Activity Calendar</h4>
          <div className="full-calender">{renderCalendar()}</div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
