// src/context/WorkoutProgressContext.js

import React, { createContext, useContext, useEffect, useState } from "react";
import workoutService from "../services/workoutService";

const WorkoutProgressContext = createContext();

export const useWorkoutProgress = () => useContext(WorkoutProgressContext);

export const WorkoutProgressProvider = ({ children }) => {
  const [dailyProgress, setDailyProgress] = useState({
    date: "",
    completed: false,
    xp: 0,
    penalties: 0,
    workoutType: "",
    accuracy: 0,
  });

  const fetchToday = async () => {
    const res = await workoutService.getTodayProgress();
    if (res?.success) setDailyProgress(res.data);
  };

  useEffect(() => {
    fetchToday();
  }, []);

  const updateWorkoutResult = ({
    exercise,
    level = "",
    score,
    xp,
    completed,
  }) => {
    const key = level || exercise; // Use level name as unique identifier
    setDailyProgress((prev) => ({
      ...prev,
      [key]: { score, xp, completed },
    }));
  };

  return (
    <WorkoutProgressContext.Provider
      value={{ dailyProgress, updateWorkoutResult }}
    >
      {children}
    </WorkoutProgressContext.Provider>
  );
};
