import axiosInstance from "./api";

const workoutService = {
  startLiveDetection: async (exercise) => {
    const response = await axiosInstance.post("/start", {
      exercise,
    });
    return response.data;
  },

  uploadVideoDetection: async (videoFile, exercise) => {
    const formData = new FormData();
    formData.append("video", videoFile);
    formData.append("exercise", exercise);

    const response = await axiosInstance.post("/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    return response.data;
  },

  logWorkout: async ({ user_id, exercise, level, score, xp, completed }) => {
    const response = await axiosInstance.post("/api/workout/log", {
      user_id,
      exercise,
      level,
      score,
      xp,
      completed,
    });
    return response.data;
  },

  getTodayProgress: async () => {
    const response = await axiosInstance.get("/workout/today");
    return response.data;
  },

  saveWorkoutResult: async ({ xp, accuracy, workoutType, level }) => {
    const response = await axiosInstance.post("/workout/save", {
      xp,
      accuracy,
      workoutType,
      level,
    });
    return response.data;
  },
};

export default workoutService;
