import React, { useEffect, useState } from "react";
import "./Profile.css";

const weekDays = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Sample progress data
  const [weekProgress, setWeekProgress] = useState([
    "✅",
    "✅",
    "",
    "",
    "",
    "",
    "",
  ]);
  const [streak, setStreak] = useState(2); // sample value

  useEffect(() => {
    const fetchStats = async () => {
      const userId = localStorage.getItem("user_id");
      if (!userId) {
        setError("User ID not found");
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(
          `http://localhost:5001/user/stats?user_id=${userId}`
        );
        if (!response.ok) throw new Error("Failed to fetch user stats");
        const data = await response.json();
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <div className="dashboard">Loading...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;

  return (
    <div className="dashboard">
      {/* LEFT CARD - Profile */}
      <div className="card profile-card-large">
        <img
          src="/default-avatar.png" // Make sure to place this avatar image in your public folder
          alt="Profile"
          className="avatar"
        />
        <div className="profile-info">
          <h3>{user.username || user.user_id}</h3>
          <p>{user.email}</p>
          <p>
            <strong>Phone:</strong> +1-000-000-0000
          </p>
        </div>
      </div>

      {/* RIGHT CARD 1 - Stats */}
      <div className="card stats-card">
        <h3>Fitness Stats</h3>
        <div className="stat-row">
          <label>Level:</label>
          <span>{user.level}</span>
        </div>
        <div className="stat-row">
          <label>XP:</label>
          <span>
            {user.xp} / {user.xpGoal}
          </span>
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{ width: `${(user.xp / user.xpGoal) * 100}%` }}
            ></div>
          </div>
        </div>
        <div className="stat-row">
          <label>Score:</label>
          <span>{user.score}</span>
        </div>
        <div className="stat-row">
          <label>Exercises Completed:</label>
          <span>{user.exercises}</span>
        </div>
        <div className="stat-row">
          <label>Total Reps:</label>
          <span>{user.reps}</span>
        </div>
        <div className="stat-row">
          <label>Goals Achieved:</label>
          <span>{user.goals}</span>
        </div>
      </div>

      {/* RIGHT CARD 2 - Streak + Calendar */}
      <div className="card calendar-card">
        <h3>🔥 Streak: {streak} Days</h3>
        <p className="hint">
          Keep going! Reach <span className="highlight">5 Days</span> for a
          reward.
        </p>
        <div className="calendar">
          {weekDays.map((day, i) => (
            <div
              key={day}
              className={`day-box ${weekProgress[i] === "✅" ? "checked" : ""}`}
            >
              {day} {weekProgress[i]}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Profile;
