import React from "react";
import "./XPProgressCard.css"; // You'll add styles here

const XPProgressCard = ({ level, xp, maxXp }) => {
  const progress = Math.min((xp / maxXp) * 100, 100).toFixed(1);
  const badge =
    level >= 10 ? "ðŸ”¥ Master" : level >= 5 ? "ðŸ¥ˆ Intermediate" : "ðŸŽ¯ Beginner";

  return (
    <div className="xp-card">
      <h3>XP & Badge Progress</h3>
      <p>
        <strong>Level:</strong> {level}
      </p>
      <p>
        <strong>XP:</strong> {xp} / {maxXp}
      </p>
      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }} />
      </div>
      <p>
        <strong>Badge:</strong> {badge}
      </p>
    </div>
  );
};

export default XPProgressCard;
