import React, { useState, useEffect, useRef } from "react";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi! How can I assist you with your fitness today?" },
  ]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  const handleSend = () => {
    if (!input.trim()) return;
    const userMessage = { from: "user", text: input };
    setMessages((msgs) => [...msgs, userMessage]);
    setInput("");

    // Simulate bot response
    setTimeout(() => {
      const response = getBotResponse(input);
      setMessages((msgs) => [...msgs, { from: "bot", text: response }]);
    }, 700);
  };

  const getBotResponse = (input) => {
    const lower = input.toLowerCase();
    if (lower.includes("workout") || lower.includes("exercise")) {
      return "Try a 30-minute full-body workout today!";
    }
    if (lower.includes("calorie") || lower.includes("diet")) {
      return "Remember to balance your macros: protein, carbs, and fats.";
    }
    return "Thanks for your message! Let me know how else I can help.";
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div style={styles.wrapper}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸ’¬ Fitness Chatbot</h2>
        <div style={styles.chatWindow}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                ...styles.message,
                alignSelf: msg.from === "user" ? "flex-end" : "flex-start",
                backgroundColor: msg.from === "user" ? "#007bff" : "#e0e0e0",
                color: msg.from === "user" ? "#fff" : "#000",
              }}
            >
              {msg.text}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>
        <div style={styles.inputArea}>
          <input
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            style={styles.input}
          />
          <button onClick={handleSend} style={styles.sendButton}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f5f7fa",
    padding: "20px",
  },
  card: {
    backgroundColor: "#ffffff",
    padding: "24px",
    borderRadius: "16px",
    boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
    width: "100%",
    maxWidth: "500px",
    display: "flex",
    flexDirection: "column",
    height: "80vh",
  },
  heading: {
    textAlign: "center",
    marginBottom: "16px",
    fontSize: "22px",
    color: "#333",
  },
  chatWindow: {
    flex: 1,
    padding: "10px",
    border: "1px solid #ddd",
    borderRadius: "12px",
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    backgroundColor: "#fafafa",
    marginBottom: "12px",
  },
  message: {
    maxWidth: "75%",
    padding: "10px 14px",
    borderRadius: "20px",
    fontSize: "15px",
    lineHeight: "1.4",
  },
  inputArea: {
    display: "flex",
    gap: "10px",
  },
  input: {
    flex: 1,
    padding: "10px 12px",
    borderRadius: "10px",
    border: "1px solid #ccc",
    fontSize: "15px",
  },
  sendButton: {
    padding: "10px 16px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontWeight: "bold",
  },
};

export default Chatbot;
