import React from "react";
import "./Leaderboard.css"; // New CSS file for responsiveness

const sampleLeaderboard = [
  { id: 1, name: "Adarsh", score: 1200, level: 10, xp: 4500 },
  { id: 2, name: "Bob", score: 1100, level: 9, xp: 3900 },
  { id: 3, name: "Charlie", score: 900, level: 8, xp: 3200 },
];

const getBadge = (rank, user) => {
  if (rank === 0) return "üèÜ Champion";
  if (rank === 1) return "ü•á Top Performer";
  if (rank === 2) return "‚ö°Ô∏è Hard Charger";
  if (user.xp >= 1000 && user.xp < 4000) return "üéØ XP Grinder";
  if (user.xp >= 4000) return "üî• Streak Master";
  return "üéñÔ∏è Member";
};

const Leaderboard = () => {
  return (
    <div className="leaderboard-wrapper">
      <div className="leaderboard-card">
        <h2 className="leaderboard-heading">üèÖ Leaderboard</h2>
        <div className="table-container">
          <table className="leaderboard-table">
            <thead>
              <tr>
                <th>Rank</th>
                <th>Name</th>
                <th>Badge</th>
                <th>Level</th>
                <th>XP</th>
                <th>Score</th>
              </tr>
            </thead>
            <tbody>
              {sampleLeaderboard.map((user, idx) => (
                <tr key={user.id}>
                  <td>{idx + 1}</td>
                  <td>{user.name}</td>
                  <td>{getBadge(idx, user)}</td>
                  <td>Lv. {user.level}</td>
                  <td>{user.xp} XP</td>
                  <td>{user.score}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
