import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import workoutService from "../../services/workoutService";

import { useWorkoutProgress } from "../../contexts/WorkoutProgressContext";

import PoseFeedback from "../../components/PoseFeedback";
import ExerciseAvatar3D from "../../components/ExerciseAvatar3D";

const LiveWorkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { updateWorkoutResult } = useWorkoutProgress();

  const { exercise, mode, level } = location.state || {};
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!exercise || !mode) {
      navigate("/dailychallenge");
      return;
    }

    const startWorkout = async () => {
      try {
        let res;
        if (mode === "webcam") {
          res = await workoutService.startLiveDetection(exercise);
        } else {
          const file = localStorage.getItem("uploadedWorkoutVideo");
          if (!file) throw new Error("No video uploaded");
          res = await workoutService.uploadVideoDetection(exercise, file);
        }

        const { output, score, accuracy, xp } = res;
        setResult({ output, score, accuracy, xp });
        updateWorkoutResult({ exercise, score, xp, completed: true });
      } catch (error) {
        console.error(error);
        setResult({ error: "Workout failed. Try again." });
      } finally {
        setIsLoading(false);
      }
    };

    startWorkout();
  }, [exercise, mode, navigate, updateWorkoutResult]);

  if (isLoading)
    return <div style={styles.loadingContainer}>Starting workout...</div>;

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{level || exercise.toUpperCase()} - Results</h2>

      {result?.error ? (
        <div style={styles.errorText}>{result.error}</div>
      ) : (
        <>
          <PoseFeedback accuracy={result.accuracy} />
          <ExerciseAvatar3D exercise={exercise} />
          <p style={styles.xpText}>
            XP Earned: <strong>{result.xp}</strong>
          </p>
          <p style={styles.scoreText}>
            Score: <strong>{result.score}</strong>
          </p>
        </>
      )}

      <button
        onClick={() => navigate("/dailychallenge")}
        style={styles.backButton}
      >
        Back to Challenges
      </button>
    </div>
  );
};

const styles = {
  container: {
    padding: 16,
    maxWidth: 600,
    margin: "40px auto",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  loadingContainer: {
    marginTop: 40,
    textAlign: "center",
    fontSize: 18,
    fontWeight: "600",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 16,
    textAlign: "center",
  },
  errorText: {
    color: "#dc2626", // red-600
    fontWeight: "600",
    textAlign: "center",
    marginBottom: 16,
  },
  xpText: {
    marginTop: 16,
    fontSize: 16,
  },
  scoreText: {
    fontSize: 16,
  },
  backButton: {
    marginTop: 24,
    backgroundColor: "#3b82f6", // blue-500
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontWeight: "600",
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
  },
};

export default LiveWorkout;
