import React from "react";
import "./AdventureMap.css";
import { Link } from "react-router-dom";

const levels = [
  { id: 1, name: "Forest Jump", status: "completed", icon: "üå≤" },
  { id: 2, name: "Mountain Push-Up", status: "unlocked", icon: "‚õ∞Ô∏è" },
  { id: 3, name: "Desert Squat", status: "locked", icon: "üèúÔ∏è" },
  { id: 4, name: "Icy Plank", status: "locked", icon: "‚ùÑÔ∏è" },
  { id: 5, name: "Crystal Temple", status: "locked", icon: "üèØ" },
];

function AdventureMap() {
  const levelPositions = [
    { x: 50, y: 300 },
    { x: 150, y: 200 },
    { x: 300, y: 250 },
    { x: 450, y: 180 },
    { x: 600, y: 250 },
  ];

  return (
    <div className="svg-map-container">
      <h2 className="map-title">üèÅ Adventure Fitness Map</h2>
      <svg width="700" height="400" viewBox="0 0 700 400">
        {/* Curved path */}
        <path
          d="M 50 300 C 150 100, 400 400, 600 250"
          stroke="#999"
          strokeWidth="6"
          fill="none"
        />

        {/* Level nodes */}
        {levels.map((level, index) => {
          const pos = levelPositions[index];
          const isLocked = level.status === "locked";
          const nodeColor =
            level.status === "completed"
              ? "#4caf50"
              : level.status === "unlocked"
              ? "#2196f3"
              : "#aaa";

          return (
            <g key={level.id} transform={`translate(${pos.x}, ${pos.y})`}>
              <circle r="25" fill={nodeColor} />
              <text x="-10" y="8" fontSize="20">
                {level.icon}
              </text>
              <text y="45" textAnchor="middle" fontSize="12" fill="#333">
                {level.name}
              </text>
              {!isLocked && (
                <a href={`/start/${level.id}`}>
                  <circle r="30" fill="transparent" />
                </a>
              )}
            </g>
          );
        })}
      </svg>
    </div>
  );
}

export default AdventureMap;
