import React, { useEffect, useState, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useWorkoutProgress } from "../../contexts/WorkoutProgressContext";
import PoseFeedback from "../../components/PoseFeedback";
import ExerciseAvatar3D from "../../components/ExerciseAvatar3D";
import workoutService from "../../services/workoutService";

const LiveWorkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { updateWorkoutResult, updateTodayProgress } = useWorkoutProgress();

  const { exercise, mode, level, user_id } = location.state || {};
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const videoRef = useRef(null);

  // Helper: Convert base64 video string to Blob for upload
  const base64ToBlob = (base64, mimeType = "video/mp4") => {
    const base64Data = base64.includes(",") ? base64.split(",")[1] : base64;
    const byteCharacters = atob(base64Data);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    return new Blob([byteArray], { type: mimeType });
  };

  useEffect(() => {
    if (!exercise || !mode || !user_id) {
      navigate("/dailychallenge");
      return;
    }

    const startWorkout = async () => {
      try {
        let res;
        if (mode === "webcam") {
          // Use workoutService to start live detection
          res = await workoutService.startLiveDetection(exercise);
        } else {
          // Video upload mode
          const base64Video = localStorage.getItem("uploadedWorkoutVideo");
          if (!base64Video) throw new Error("No video uploaded");

          const videoBlob = base64ToBlob(base64Video);
          res = await workoutService.uploadVideoDetection(videoBlob, exercise);
        }

        const { output, score, accuracy, xp, feedback } = res;
        setResult({ output, score, accuracy, xp });
        setFeedbackMessage(feedback || "Good effort! Keep going!");

        // Log workout using service
        await workoutService.logWorkout({
          user_id,
          exercise,
          level,
          score,
          xp,
          completed: true,
        });

        updateWorkoutResult({ exercise, score, xp, completed: true });

        // Get today's progress and update context
        const todayProgress = await workoutService.getTodayProgress();
        updateTodayProgress(todayProgress);

        // Unlock next level if conditions met
        if (score > 70 && xp > 50) {
          await workoutService.unlockNextLevel({
            user_id,
            currentLevel: level,
          });
        }
      } catch (error) {
        console.error(error);
        setResult({ error: "Workout failed. Try again." });
      } finally {
        setIsLoading(false);
      }
    };

    startWorkout();
  }, [
    exercise,
    mode,
    level,
    user_id,
    navigate,
    updateWorkoutResult,
    updateTodayProgress,
  ]);

  // Setup webcam stream
  useEffect(() => {
    if (mode === "webcam") {
      (async () => {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({
            video: true,
          });
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
            await videoRef.current.play();
          }
        } catch (err) {
          console.error("Camera access error:", err);
        }
      })();
    }
  }, [mode]);

  const renderLiveCamera = () => (
    <div style={{ marginBottom: 16 }}>
      <h4>Live Camera Feed</h4>
      <video ref={videoRef} autoPlay playsInline style={{ width: "100%" }} />
    </div>
  );

  if (isLoading)
    return (
      <div style={{ marginTop: 40, textAlign: "center" }}>
        Starting workout...
      </div>
    );

  return (
    <div style={{ padding: 16, maxWidth: 600, margin: "40px auto" }}>
      <h2 style={{ fontSize: 24, textAlign: "center" }}>
        {level || exercise.toUpperCase()} - Results
      </h2>

      {mode === "webcam" && renderLiveCamera()}

      {result?.error ? (
        <div
          style={{ color: "#dc2626", fontWeight: "600", textAlign: "center" }}
        >
          {result.error}
        </div>
      ) : (
        <>
          <PoseFeedback accuracy={result.accuracy} />
          <ExerciseAvatar3D exercise={exercise} />
          <p>
            <strong>Feedback:</strong> {feedbackMessage}
          </p>
          <p>
            <strong>XP Earned:</strong> {result.xp}
          </p>
          <p>
            <strong>Score:</strong> {result.score}
          </p>
          <p>
            <strong>Accuracy:</strong> {result.accuracy}%
          </p>
        </>
      )}

      <button
        onClick={() => navigate("/dailychallenge")}
        style={{
          marginTop: 24,
          backgroundColor: "#3b82f6",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
        }}
      >
        Back to Challenges
      </button>
    </div>
  );
};

export default LiveWorkout;
