import React, { useEffect, useState, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useWorkoutProgress } from "../../contexts/WorkoutProgressContext";
import PoseFeedback from "../../components/PoseFeedback";
import ExerciseAvatar3D from "../../components/ExerciseAvatar3D";
import { startPoseDetection } from "../../utils/poseDetectionFrontend";
import workoutService from "../../services/workoutService";

const LiveWorkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { updateWorkoutResult, updateTodayProgress } = useWorkoutProgress();

  const { exercise, mode, level, user_id } = location.state || {};
  const [result, setResult] = useState({ accuracy: null });

  const [isLoading, setIsLoading] = useState(true);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const [isVideoReady, setIsVideoReady] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    if (!exercise || !mode || !user_id) {
      navigate("/dailychallenge");
      return;
    }

    if (mode === "webcam" && !isVideoReady) return;

    const runWorkout = async () => {
      try {
        let workoutResult;

        if (mode === "webcam") {
          workoutResult = await handleWebcamMode(exercise, videoRef);
        } else {
          workoutResult = await handleVideoMode(exercise);
        }

        const { score, accuracy, xp, feedback, reps } = workoutResult;

        setResult(workoutResult);
        setFeedbackMessage(feedback || "Good effort! Keep going!");

        // Log workout in backend via workoutService
        await workoutService.logWorkout({
          user_id,
          exercise,
          level,
          score,
          xp,
          reps: reps || 0,
          completed: true,
        });

        updateWorkoutResult({ exercise, score, xp, completed: true });

        // Get today's progress via workoutService
        const todayProgress = await workoutService.getTodayProgress(user_id);
        updateTodayProgress(todayProgress);

        // Unlock next level (if eligible)
        if (score >= 70 && xp >= 50) {
          await workoutService.unlockNextLevel({
            user_id,
            currentLevel: level,
          });
        }
      } catch (error) {
        console.error(error);
        setResult({ error: "Workout failed. Try again." });
      } finally {
        setIsLoading(false);
      }
    };

    runWorkout();
  }, [
    exercise,
    mode,
    level,
    user_id,
    navigate,
    updateWorkoutResult,
    updateTodayProgress,
    isVideoReady,
  ]);

  const handleWebcamMode = async (exercise, videoRef) => {
    // Wait for video to be ready
    await new Promise((resolve, reject) => {
      const video = videoRef.current;
      if (!video) return reject("Video element not found");

      if (video.readyState >= 2) {
        // Video metadata is already loaded
        resolve();
      } else {
        // Wait for loadedmetadata event
        const onLoadedMetadata = () => {
          video.removeEventListener("loadedmetadata", onLoadedMetadata);
          resolve();
        };
        video.addEventListener("loadedmetadata", onLoadedMetadata);
      }
    });

    const detectionResult = await startPoseDetection(
      videoRef.current,
      exercise,
      (feedback) => setFeedbackMessage(feedback) // live updates
    );

    return {
      ...detectionResult,
      feedback: detectionResult.feedback || "Live detection complete!",
      reps: detectionResult.reps || 0,
    };
  };

  const handleVideoMode = async (exercise) => {
    const base64Video = localStorage.getItem("uploadedWorkoutVideo");
    if (!base64Video) throw new Error("No video uploaded");

    const res = await workoutService.uploadVideoDetection(
      base64Video,
      exercise
    );
    return res;
  };

  useEffect(() => {
    let timeoutId;

    if (mode === "webcam" && videoRef.current) {
      navigator.mediaDevices
        .getUserMedia({ video: true })
        .then((stream) => {
          videoRef.current.srcObject = stream;
          videoRef.current
            .play()
            .then(() => setIsVideoReady(true))
            .catch((err) => {
              console.error("Video play error:", err);
              setIsLoading(false);
            });
        })
        .catch((err) => {
          console.error("Camera access error:", err);
          setIsLoading(false);
        });

      // Fallback timeout in case nothing happens (10 seconds)
      timeoutId = setTimeout(() => {
        if (!isVideoReady) {
          console.warn("Timeout: video not ready in time.");
          setIsLoading(false);
          setResult({ error: "Camera failed to load. Please try again." });
        }
      }, 10000); // 10 seconds
    }

    return () => {
      clearTimeout(timeoutId);
    };
  }, [mode]);

  useEffect(() => {
    return () => {
      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const renderLiveCamera = () => (
    <div style={{ marginBottom: 16 }}>
      <h4>Live Camera Feed</h4>
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        style={{ width: "100%" }}
      />
    </div>
  );

  {
    isLoading && (
      <div style={{ marginTop: 40, textAlign: "center" }}>
        <div className="loader"></div>
        <p>Starting workout...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: 16, maxWidth: 600, margin: "40px auto" }}>
      <h2 style={{ fontSize: 24, textAlign: "center" }}>
        {level || exercise.toUpperCase()} - Results
      </h2>

      {mode === "webcam" && renderLiveCamera()}

      {result?.error ? (
        <div
          style={{ color: "#dc2626", fontWeight: "600", textAlign: "center" }}
        >
          {result.error}
        </div>
      ) : (
        <>
          <PoseFeedback accuracy={result.accuracy} />
          <ExerciseAvatar3D exercise={exercise} />
          <p>
            <strong>Feedback:</strong> {feedbackMessage}
          </p>
          <p>
            <strong>XP Earned:</strong> {result.xp}
          </p>
          <p>
            <strong>Score:</strong> {result.score}
          </p>
          <p>
            <strong>Accuracy:</strong>{" "}
            {result.accuracy !== null && result.accuracy !== undefined ? (
              <p>Accuracy: {result.accuracy.toFixed(2)}%</p>
            ) : (
              <p>Accuracy: Loading...</p> // or whatever message you prefer
            )}
          </p>
          {result.reps !== undefined && (
            <p>
              <strong>Repetitions:</strong> {result.reps}
            </p>
          )}
        </>
      )}

      <button
        onClick={() => navigate("/dailychallenge")}
        style={{
          marginTop: 24,
          backgroundColor: "#3b82f6",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
        }}
      >
        Back to Challenges
      </button>
    </div>
  );
};

export default LiveWorkout;
