// src/pages/LiveWorkout/LiveWorkout.js
import React, { useState } from "react";
import axios from "axios";
import "./LiveWorkout.css"; // Import custom CSS

function LiveWorkout() {
  const [result, setResult] = useState("");
  const [video, setVideo] = useState(null);
  const [trackingType, setTrackingType] = useState("jump");

  const handleStartTracking = async () => {
    const res = await axios.post("http://localhost:5001/start", {
      type: trackingType,
    });
    setResult(res.data.message);
  };

  const handleVideoUpload = async () => {
    const formData = new FormData();
    formData.append("video", video);
    formData.append("type", trackingType);

    const res = await axios.post("http://localhost:5001/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    setResult(res.data.message);
  };

  return (
    <div className="liveworkout-container">
      <h2 className="liveworkout-title">üé• Live Workout Tracker</h2>

      <div className="liveworkout-dropdown">
        <label className="liveworkout-label">Choose Exercise Type:</label>
        <select
          className="liveworkout-select"
          onChange={(e) => setTrackingType(e.target.value)}
        >
          <option value="jump">üèÉ Jump</option>
          <option value="pushup">üí™ Push-Up</option>
          <option value="squat">ü¶µ Squat</option>
          <option value="plank">üßò Plank</option>
        </select>
      </div>

      <div className="liveworkout-buttons">
        <button onClick={handleStartTracking} className="start-button">
          ‚ñ∂Ô∏è Start Live Webcam Tracking
        </button>

        <div className="upload-section">
          <input
            type="file"
            accept="video/*"
            className="upload-input"
            onChange={(e) => setVideo(e.target.files[0])}
          />
          <button onClick={handleVideoUpload} className="upload-button">
            ‚¨ÜÔ∏è Upload Workout Video
          </button>
        </div>
      </div>

      <div className="result-section">
        <h3 className="result-title">üßæ Result</h3>
        <p className="result-message">{result || "No result yet."}</p>
      </div>
    </div>
  );
}

export default LiveWorkout;
