import React from "react";
import { useNavigate } from "react-router-dom";
import "./AdventureMap.css";

// Generate 30 levels dynamically
const exerciseTypes = ["jump", "squat", "pushup"];
const levelIcons = {
  jump: "ðŸ¦˜",
  squat: "ðŸ§",
  pushup: "ðŸ’ª",
};

const levels = Array.from({ length: 30 }, (_, i) => {
  const id = i + 1;
  const exercise = exerciseTypes[i % exerciseTypes.length];
  const reps = 5 + Math.floor(i / 3) * 5; // reps increase every 3 levels
  const name = `${exercise[0].toUpperCase() + exercise.slice(1)} Lv${id}`;
  const description = `${
    exercise.charAt(0).toUpperCase() + exercise.slice(1)
  } ${reps} times`;

  return {
    id,
    name,
    description,
    reps,
    exercise,
    icon: levelIcons[exercise],
  };
});

const AdventureMap = () => {
  const navigate = useNavigate();

  const handleLevelSelect = (level) => {
    navigate("/select-workout", {
      state: {
        levelId: level.id,
        levelName: level.name,
        exercise: level.exercise,
        targetReps: level.reps,
      },
    });
  };

  return (
    <div className="map-container">
      <h2 className="map-title">Choose Your Adventure</h2>
      <div className="level-zigzag">
        {levels.map((level, index) => (
          <div
            key={level.id}
            className={`level-card ${index % 2 === 0 ? "left" : "right"}`}
            onClick={() => handleLevelSelect(level)}
          >
            <div className="level-icon">{level.icon}</div>
            <div>
              <h3>{level.name}</h3>
              <p>{level.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdventureMap;
