import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Button from "../../components/Button";

const ExerciseSelector = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [exercise, setExercise] = useState(location.state?.exercise || "jump");
  const [mode, setMode] = useState("webcam");
  const [videoFile, setVideoFile] = useState(null);

  const handleStart = () => {
    if (mode === "upload" && !videoFile) {
      alert("Please upload a video.");
      return;
    }

    navigate("/liveworkout", {
      state: {
        exercise,
        mode,
        videoFile,
      },
    });
  };

  // Inline styles object
  const styles = {
    container: {
      padding: "16px",
      maxWidth: "400px",
      margin: "0 auto",
      textAlign: "center",
      fontFamily: "Arial, sans-serif",
    },
    heading: {
      fontSize: "24px",
      fontWeight: "700",
      marginBottom: "16px",
      color: "#111827", // dark slate
    },
    label: {
      display: "block",
      marginBottom: "8px",
      textAlign: "left",
      fontWeight: "600",
      color: "#374151", // gray-700
    },
    select: {
      width: "100%",
      padding: "8px",
      borderRadius: "6px",
      border: "1px solid #D1D5DB", // gray-300
      marginBottom: "16px",
      fontSize: "16px",
      outline: "none",
    },
    radioGroup: {
      display: "flex",
      justifyContent: "space-around",
      marginBottom: "16px",
    },
    radioLabel: {
      fontSize: "16px",
      color: "#374151",
      cursor: "pointer",
      userSelect: "none",
    },
    fileInput: {
      marginBottom: "16px",
      width: "100%",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Choose Your Workout</h2>

      <label style={styles.label}>Exercise Type:</label>
      <select
        value={exercise}
        onChange={(e) => setExercise(e.target.value)}
        style={styles.select}
        disabled={!!location.state?.exercise} // Disable only if passed from map
      >
        <option value="jump">Jump</option>
        <option value="squat">Squat</option>
        <option value="pushup">Push-up</option>
        <option value="plank">Plank</option>
      </select>

      <label style={styles.label}>Mode:</label>
      <div style={styles.radioGroup}>
        <label style={styles.radioLabel}>
          <input
            type="radio"
            value="webcam"
            checked={mode === "webcam"}
            onChange={(e) => setMode(e.target.value)}
            style={{ marginRight: "6px", cursor: "pointer" }}
          />
          Webcam
        </label>
        <label style={styles.radioLabel}>
          <input
            type="radio"
            value="upload"
            checked={mode === "upload"}
            onChange={(e) => setMode(e.target.value)}
            style={{ marginRight: "6px", cursor: "pointer" }}
          />
          Upload Video
        </label>
      </div>

      {mode === "upload" && (
        <input
          type="file"
          accept="video/*"
          onChange={(e) => setVideoFile(e.target.files[0])}
          style={styles.fileInput}
        />
      )}

      <Button onClick={handleStart} text="Start Workout" />
    </div>
  );
};

export default ExerciseSelector;
