import axios from "./api";

const workoutService = {
  // Existing methods
  startLiveDetection: async (exercise) => {
    const response = await axios.post("/workout/live", { exercise });
    return response.data;
  },

  uploadVideoDetection: async (exercise, videoFile) => {
    const formData = new FormData();
    formData.append("exercise", exercise);
    formData.append("video", videoFile);

    const response = await axios.post("/workout/upload", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    return response.data;
  },
  logWorkout: async ({ user_id, exercise, level, score, xp, completed }) => {
    const response = await axios.post("/workout/log", {
      user_id,
      exercise,
      level,
      score,
      xp,
      completed,
    });
    return response.data;
  },

  // Add these two:
  getTodayProgress: async () => {
    const response = await axios.get("/workout/today");
    return response.data;
  },

  saveWorkoutResult: async ({ xp, accuracy, workoutType }) => {
    const response = await axios.post("/workout/save", {
      xp,
      accuracy,
      workoutType,
      level,
    });
    return response.data;
  },
};

export default workoutService;
