// src/pages/LiveWorkout.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import workoutService from "../../services/workoutService";
import { useWorkoutProgress } from "../../contexts/WorkoutProgressContext";
import PoseFeedback from "../../components/PoseFeedback";
import ExerciseAvatar3D from "../../components/ExerciseAvatar3D";

const LiveWorkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { updateWorkoutResult } = useWorkoutProgress();

  const { exercise, mode, level } = location.state || {};
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!exercise || !mode) {
      navigate("/dailychallenge");
      return;
    }

    const startWorkout = async () => {
      try {
        let res;
        if (mode === "webcam") {
          res = await workoutService.startLiveDetection(exercise);
        } else {
          const file = localStorage.getItem("uploadedWorkoutVideo");
          if (!file) throw new Error("No video uploaded");
          res = await workoutService.uploadVideoDetection(exercise, file);
        }

        const { output, score, accuracy, xp } = res;
        setResult({ output, score, accuracy, xp });
        updateWorkoutResult({ exercise, score, xp, completed: true });
      } catch (error) {
        console.error(error);
        setResult({ error: "Workout failed. Try again." });
      } finally {
        setIsLoading(false);
      }
    };

    startWorkout();
  }, [exercise, mode, navigate, updateWorkoutResult]);

  if (isLoading)
    return (
      <div style={{ marginTop: 40, textAlign: "center" }}>
        Starting workout...
      </div>
    );

  return (
    <div style={{ padding: 16, maxWidth: 600, margin: "40px auto" }}>
      <h2 style={{ fontSize: 24, textAlign: "center" }}>
        {level || exercise.toUpperCase()} - Results
      </h2>

      {result?.error ? (
        <div
          style={{ color: "#dc2626", fontWeight: "600", textAlign: "center" }}
        >
          {result.error}
        </div>
      ) : (
        <>
          <PoseFeedback accuracy={result.accuracy} />
          <ExerciseAvatar3D exercise={exercise} />
          <p>
            XP Earned: <strong>{result.xp}</strong>
          </p>
          <p>
            Score: <strong>{result.score}</strong>
          </p>
        </>
      )}

      <button
        onClick={() => navigate("/dailychallenge")}
        style={{
          marginTop: 24,
          backgroundColor: "#3b82f6",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
        }}
      >
        Back to Challenges
      </button>
    </div>
  );
};

export default LiveWorkout;
