import React from "react";
import "./AdventureMap.css";
import { Link } from "react-router-dom";

const levels = [
  {
    id: 1,
    name: "Forest Jump",
    status: "completed",
    icon: "üå≤",
    combos: [
      {
        id: "1-1",
        name: "Forest Trial I",
        exercises: [
          { type: "jump", reps: 10 },
          { type: "pushup", reps: 5 },
        ],
        status: "completed",
      },
    ],
  },
  {
    id: 2,
    name: "Mountain Push-Up",
    task: "Do 10 Push-Ups",
    status: "unlocked",
    icon: "‚õ∞Ô∏è",
  },
  {
    id: 3,
    name: "Desert Squat",
    task: "15 Squats",
    status: "locked",
    icon: "üèúÔ∏è",
  },
  {
    id: 4,
    name: "Icy Plank",
    task: "30s Plank",
    status: "locked",
    icon: "‚ùÑÔ∏è",
  },
];

function AdventureMap() {
  return (
    <div className="adventure-map-wrapper">
      <h1 className="map-title">üèÅ Adventure Fitness Map</h1>
      <div className="zigzag-path">
        {levels.map((level, index) => (
          <div
            key={level.id}
            className={`level-node ${level.status} ${
              index % 2 === 0 ? "left" : "right"
            }`}
          >
            <div className="level-content">
              <div className="icon">{level.icon}</div>
              <h3>{level.name}</h3>
              {level.task && <p>{level.task}</p>}
              {level.combos?.map((combo) => (
                <div key={combo.id} className={`combo-box ${combo.status}`}>
                  <h4>{combo.name}</h4>
                  <ul>
                    {combo.exercises.map((ex, i) => (
                      <li key={i}>
                        {ex.type} ‚Äì {ex.reps || ex.time}
                      </li>
                    ))}
                  </ul>
                  {combo.status !== "locked" ? (
                    <Link
                      to={`/liveworkout?combo=${combo.id}`}
                      className="start-combo-btn"
                    >
                      Start
                    </Link>
                  ) : (
                    <button className="locked-btn" disabled>
                      üîí Locked
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdventureMap;
