import React, { useState } from "react";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    "ðŸ‘‹ Hi! I'm your virtual coach. Ask me anything!",
  ]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (!input.trim()) return;
    const reply = `ðŸ¤– Great question! (Pretend answer to: "${input}")`;
    setMessages((prev) => [...prev, `You: ${input}`, reply]);
    setInput("");
  };

  return (
    <div className="page">
      <h1>ðŸ§  Chatbot</h1>
      <div style={{ marginBottom: "1rem" }}>
        {messages.map((msg, i) => (
          <div key={i}>{msg}</div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type your fitness question..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chatbot;
