// src/pages/Home/Home.js
import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Welcome to Fitness Adventure</h1>
      <p style={styles.subtitle}>
        Your journey to a healthier life starts here.
      </p>
      <div style={styles.buttons}>
        <Link to="/daily-challenge" style={styles.button}>
          Daily Challenge
        </Link>
        <Link to="/adventure-map" style={styles.button}>
          Adventure Map
        </Link>
        <Link to="/profile" style={styles.button}>
          Profile
        </Link>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "600px",
    margin: "40px auto",
    textAlign: "center",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  title: {
    fontSize: "2.5rem",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "1.2rem",
    marginBottom: "30px",
    color: "#555",
  },
  buttons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
  },
  button: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "white",
    textDecoration: "none",
    borderRadius: "5px",
    fontWeight: "600",
  },
};

export default Home;
