// src/pages/LiveWorkout/LiveWorkout.js
import React, { useState } from "react";
import axios from "axios";

function LiveWorkout() {
  const [result, setResult] = useState("");
  const [video, setVideo] = useState(null);
  const [trackingType, setTrackingType] = useState("jump"); // default

  const handleStartTracking = async () => {
    const res = await axios.post("http://localhost:5001/start", {
      type: trackingType,
    });
    setResult(res.data.message);
  };

  const handleVideoUpload = async () => {
    const formData = new FormData();
    formData.append("video", video);
    formData.append("type", trackingType);

    const res = await axios.post("http://localhost:5001/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    setResult(res.data.message);
  };

  return (
    <div className="container">
      <h2>ðŸŽ¥ Live Workout Tracking</h2>
      <select onChange={(e) => setTrackingType(e.target.value)}>
        <option value="jump">Jump</option>
        <option value="pushup">Push-Up</option>
        <option value="squat">Squat</option>
        <option value="plank">Plank</option>
      </select>

      <div>
        <button onClick={handleStartTracking}>
          Start Real-time via Webcam
        </button>
      </div>

      <div>
        <input type="file" onChange={(e) => setVideo(e.target.files[0])} />
        <button onClick={handleVideoUpload}>Upload Video</button>
      </div>

      <div>
        <h3>ðŸ§¾ Result</h3>
        <p>{result}</p>
      </div>
    </div>
  );
}

export default LiveWorkout;
