// src/services/api.js
import axios from "axios";

const API_BASE_URL = "http://localhost:5001";

const axiosInstance = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true,
});

export const startWorkout = async (type) => {
  try {
    const response = await axiosInstance.post("/start", { type });
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

export const uploadVideo = async (file, type) => {
  const formData = new FormData();
  formData.append("video", file);
  formData.append("type", type);

  try {
    const response = await axiosInstance.post("/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

export default axiosInstance; // export default axios instance for direct import if needed
