import React, { useState } from "react";
import axios from "axios";
import "./LiveWorkout.css";

const levels = [
  { type: "jump", label: "🏃 Jump" },
  { type: "pushup", label: "💪 Push-Up" },
  { type: "squat", label: "🦵 Squat" },
  { type: "plank", label: "🧘 Plank" },
];

function LiveWorkout() {
  const [currentLevel, setCurrentLevel] = useState(0);
  const [points, setPoints] = useState(0);
  const [video, setVideo] = useState(null);
  const [result, setResult] = useState("");

  const exercise = levels[currentLevel];

  const handleStartTracking = async () => {
    try {
      const res = await axios.post("http://localhost:5001/start", {
        type: exercise.type,
      });

      if (res.data.success) {
        setResult(res.data.output || "Workout tracking started!");
        advanceLevel();
      } else {
        setResult(res.data.error || "Failed to start tracking.");
      }
    } catch (err) {
      setResult("Error during tracking.");
      console.error(err);
    }
  };

  const handleVideoUpload = async () => {
    if (!video) {
      setResult("Please select a video file first.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("video", video);
      formData.append("type", exercise.type);

      const res = await axios.post("http://localhost:5001/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setResult(
          res.data.output || "Video uploaded and processed successfully!"
        );
        advanceLevel();
      } else {
        setResult(res.data.error || "Video processing failed.");
      }
    } catch (err) {
      setResult("Error during upload.");
      console.error(err);
    } finally {
      setVideo(null); // Reset selected video
    }
  };

  const advanceLevel = () => {
    if (currentLevel < levels.length - 1) {
      setCurrentLevel(currentLevel + 1);
      setPoints(points + 10);
    } else {
      setPoints(points + 10);
      setResult("🎉 You've completed all levels!");
    }
  };

  return (
    <div className="liveworkout-container">
      <h2 className="liveworkout-title">🔥 Multi-Level Workout Tracker</h2>

      <div className="level-info">
        <p>
          <strong>Current Level:</strong> {currentLevel + 1} - {exercise.label}
        </p>
        <p>
          <strong>Points:</strong> {points}
        </p>
      </div>

      <div className="liveworkout-buttons">
        <button onClick={handleStartTracking} className="start-button">
          ▶️ Start {exercise.label} Live Tracking
        </button>

        <div className="upload-section">
          <input
            type="file"
            accept="video/*"
            className="upload-input"
            onChange={(e) => setVideo(e.target.files[0])}
            value={video ? video.name : ""}
          />
          <button onClick={handleVideoUpload} className="upload-button">
            ⬆️ Upload {exercise.label} Video
          </button>
        </div>
      </div>

      <div className="result-section">
        <h3 className="result-title">🧾 Result</h3>
        <p className="result-message">{result || "No result yet."}</p>
      </div>
    </div>
  );
}

export default LiveWorkout;
