import * as tf from "@tensorflow/tfjs";
import * as posedetection from "@tensorflow-models/pose-detection";
import "@tensorflow/tfjs-backend-webgl";

let detector;
let audio = {
  good: new Audio("/audio/good.mp3"),
  bad: new Audio("/audio/bad.mp3"),
};

export async function loadModel() {
  await tf.setBackend("webgl");
  await tf.ready();
  detector = await posedetection.createDetector(
    posedetection.SupportedModels.MoveNet,
    {
      modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
    }
  );
}

function getY(keypoints, name) {
  return keypoints.find((k) => k.name === name)?.y || 0;
}

function getX(keypoints, name) {
  return keypoints.find((k) => k.name === name)?.x || 0;
}

function speakFeedback(msg) {
  const speech = new SpeechSynthesisUtterance(msg);
  speech.volume = 1;
  speech.pitch = 1;
  speech.rate = 1;
  window.speechSynthesis.speak(speech);
}

function validateAndCount(pose, exercise, state) {
  const k = pose.keypoints;
  let feedback = "";
  let scorePenalty = 0;
  let repCounted = false;

  if (!k || k.length === 0) return { feedback, scorePenalty, repCounted };

  const hipY = getY(k, "left_hip");
  const kneeY = getY(k, "left_knee");
  const shoulderY = getY(k, "left_shoulder");
  const ankleY = getY(k, "left_ankle");
  const elbowY = getY(k, "left_elbow");

  switch (exercise) {
    case "squat":
      if (hipY > kneeY + 30) {
        if (!state.down) {
          state.down = true;
          feedback = "Good depth!";
          repCounted = true;
          audio.good.play();
        }
      } else if (hipY < kneeY - 30) {
        state.down = false;
      } else {
        feedback = "Go deeper!";
        scorePenalty = 5;
        audio.bad.play();
      }
      break;

    case "pushup":
      if (shoulderY > elbowY + 20) {
        if (!state.down) {
          state.down = true;
          feedback = "Good pushup!";
          repCounted = true;
          audio.good.play();
        }
      } else if (shoulderY < elbowY - 10) {
        state.down = false;
      } else {
        feedback = "Lower your chest!";
        scorePenalty = 5;
        audio.bad.play();
      }
      break;

    case "jump": {
      const hipY = getY(k, "left_hip");

      if (hipY < state.prevHipY - 20) {
        if (!state.jumping) {
          state.jumping = true;
          feedback = "Nice jump!";
          repCounted = true;
          audio.good.play();
        }
      } else {
        state.jumping = false;
      }

      state.prevHipY = hipY;
      break;
    }

    case "plank":
      const diff = Math.abs(hipY - shoulderY);
      if (diff > 40) {
        feedback = "Straighten your back!";
        scorePenalty = 5;
        audio.bad.play();
      }
      break;
  }

  return { feedback, scorePenalty, repCounted };
}

export async function startPoseDetection(
  videoElement,
  exercise,
  targetReps = 10,
  feedbackCallback = null // ✅ added live feedback support
) {
  if (!detector) await loadModel();

  if (!videoElement) throw new Error("Video element is null");
  if (videoElement.videoWidth === 0 || videoElement.videoHeight === 0) {
    throw new Error("Video element has no dimensions yet");
  }

  const canvas = document.createElement("canvas");
  canvas.width = videoElement.videoWidth;
  canvas.height = videoElement.videoHeight;
  document.body.appendChild(canvas);
  const ctx = canvas.getContext("2d");

  let totalScore = 100;
  let badFormCount = 0;
  let repCount = 0;
  const state = { down: false, jumping: false, prevHipY: 0 };

  return new Promise((resolve) => {
    const timeout = setTimeout(() => {
      console.warn("Timeout: ending detection early.");
      document.body.removeChild(canvas);
      return resolve(null);
    }, 30000);

    const detect = async () => {
      const poses = await detector.estimatePoses(videoElement);
      const pose = poses[0];

      const { feedback, scorePenalty, repCounted } = validateAndCount(
        pose,
        exercise,
        state
      );

      if (feedback) {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.font = "20px Arial";
        ctx.fillStyle = scorePenalty > 0 ? "red" : "green";
        ctx.fillText(feedback, 10, 30);
        speakFeedback(feedback);
      }

      if (scorePenalty > 0) {
        totalScore -= scorePenalty;
        badFormCount++;
      }

      if (repCounted) {
        repCount++;
      }

      // ✅ Send live data to frontend (if callback provided)
      if (typeof feedbackCallback === "function") {
        feedbackCallback({
          reps: repCount,
          formFeedback: feedback || "Keep going!",
        });
      }

      if (repCount >= targetReps) {
        clearTimeout(timeout);
        document.body.removeChild(canvas);
        return resolve({
          score: Math.max(0, totalScore),
          accuracy: Math.floor(
            ((targetReps - badFormCount) / targetReps) * 100
          ),
          xp: Math.floor(Math.max(0, totalScore * 0.8)),
          feedback:
            badFormCount > 0 ? "Try better form next time!" : "Perfect form!",
          reps: repCount,
          badFormCount,
        });
      }

      requestAnimationFrame(detect);
    };

    detect();
  });
}
